import { Component } from "@angular/core";
import { NgForm } from "@angular/forms";

@Component({
  selector: "app-todolist",
  templateUrl: "./todolist.component.html",
  styleUrls: ["./todolist.component.css"],
})
export class TodolistComponent {
  taskArray = [
    { taskName: "Comprar carne moída", isCompleted: false, isEditable: false },
    { taskName: "Ir na Creusa", isCompleted: false, isEditable: false },
    {
      taskName: "Comprar peça do monza na feira da marreta",
      isCompleted: false,
      isEditable: false,
    },
    {
      taskName: "Fazer o Palmeiras ganhar um mundial",
      isCompleted: false,
      isEditable: false,
    },
  ];

  ngOnInit(): void {
    this.getFromLocalStorage();
  }

  onSubmit(form: NgForm) {
    console.log(form);

    this.taskArray.push({
      taskName: form.controls["tarefa"].value,
      isCompleted: false,
      isEditable: false,
    });

    this.saveToLocalStorage();

    form.reset();
  }

  saveToLocalStorage() {
    let stringJSON = JSON.stringify(this.taskArray);
    localStorage.setItem("todolist", stringJSON);
  }

  getFromLocalStorage() {
    let itemsJSONString = localStorage.getItem("todolist");
    if (itemsJSONString != null) {
      this.taskArray = JSON.parse(itemsJSONString);
    }
  }

  deleteAll<T>(taskArray: T[]) {
    taskArray.length = 0;

    this.saveToLocalStorage();
  }

  onDelete(index: number) {
    console.log(index);
    this.taskArray.splice(index, 1);

    this.saveToLocalStorage();
  }

  onCheck(index: number) {
    this.taskArray[index].isCompleted = !this.taskArray[index].isCompleted;
    console.log(this.taskArray[index]);

    this.saveToLocalStorage();
  }

  onSave(index: number, newtask: string) {
    this.taskArray[index].taskName = newtask;
    this.taskArray[index].isEditable = false;

    this.saveToLocalStorage();
  }

  onEdit(index: number) {
    this.taskArray[index].isEditable = true;

    this.saveToLocalStorage();
  }
}
<div class="container">
  <h1>To-do Application</h1>

  <hr />

  <!--Inicio da lista de tarefas-->
  <form id="submitForm" (ngSubmit)="onSubmit(taskForm)" #taskForm="ngForm">
    <div class="form-group">
      <label for="tarefa">Coisas a fazer</label>

      <!-- Campo de texto do projeto -->
      <input
        type="text"
        class="form-control"
        id="tarefa"
        placeholder="O que tenho que fazer hoje...."
        ngModel
        name="tarefa"
        required
      />

      <!-- Mensagem de erro -->
      <small
        *ngIf="taskForm.invalid"
        id="errorMessage"
        class="form-text text-danger"
      >
        Campo obrigatório</small
      >
    </div>

    <!--Botão para submeter a tarefa na lista de tarefas-->
    <button
      [disabled]="taskForm.invalid"
      type="submit"
      class="btn btn-primary"
      id="submitBtn"
    >
      Submit
    </button>

    <!--Botão para deletar tudo do formulario-->
    <button
      id="deleteAll"
      type="button"
      class="btn btn-danger"
      id="deleteAll"
      (click)="deleteAll(taskArray)"
    >
      Delete All
    </button>

    <!--Colunas das tabelas-->
    <table class="table">
      <thead>
        <tr>
          <th scope="col">Tarefa</th>
          <th scope="col">Completa</th>
          <th scope="col">Deletar</th>
          <th scope="col">Editar</th>
          <th scope="col">Salvar</th>
        </tr>
      </thead>

      <!--Um 'laço' para criar novas linhas com as novas tarefas salvas-->
      <tbody>
        <tr *ngFor="let t of taskArray; index as i">
          <!--Uma nova linha com a nova tarefa que foi submetida-->
          <td>
            <input
              #taskInput
              type="text"
              class="form-control"
              id="tarefa"
              value="{{ t.taskName }}"
              [readOnly]="!t.isEditable"
              [ngClass]="{}"
            />
          </td>

          <!--Uma caixa 'checkbox' para a nova tarefa-->
          <td>
            <input
              type="checkbox"
              (change)="onCheck(i)"
              [checked]="t.isCompleted"
            />
          </td>

          <!--Botão de deletar tarefa-->
          <td>
            <button
              id="deletBtn"
              type="button"
              class="btn btn-danger btn-sm"
              (click)="onDelete(i)"
            >
              Deletar
            </button>
          </td>

          <!--Botão de editar tarefa-->
          <td>
            <button
              id="editBtn"
              type="button"
              class="btn btn-primary btn-sm"
              (click)="onEdit(i)"
              [disabled]="t.isEditable"
            >
              Editar
            </button>
          </td>

          <!--Botão de salvar tarefa editada-->
          <td>
            <button
              id="saveBtn"
              type="button"
              class="btn btn-warning btn-sm"
              (click)="onSave(i, taskInput.value)"
              [disabled]="!t.isEditable"
            >
              Salvar
            </button>
          </td>
        </tr>
      </tbody>
    </table>
  </form>
</div>
